mapper.update:
update beginnerBoard
    set title=#{title}, content=#{content}, writer=#{writer}, notice=#{notice}
    where post_num=#{postNum}

UPDATE beginnerBoard
    SET VIEWS= VIEWS + 1
    WHERE POST_NUM=#{postNum}

UPDATE beginnerboard
    SET recommend_count = (SELECT COUNT(*) FROM beginnerboard_recommend WHERE post_Num=#{postNum})
    WHERE post_Num=#{postNum}

mapper.select:
SELECT recommend_count
    FROM beginnerboard
    WHERE post_Num=#{postNum}
